mapper.select:
select ex.student_id studentId,ex.paper_id paperId,ex.total_score resultScore, paper.title paperTitle,paper.total_score totalScore, student.name studentName from exam_result ex left join paper on ex.paper_id = paper.id left join student on student.student_id = ex.student_id
and ex.paper_id = #{paperId}
and ex.student_id = #{studentId}
